import React, {useContext} from 'react';
import {BrowserRouter as Router, Navigate, Route, Routes} from 'react-router-dom';
import LoginPage from "../components/login/LoginPage";
import DashboardRouter from "./DashboardRouter";
import {AuthContext} from "../auth/AuthContext";
import { useParams} from "react-router";




const AppRouter = ({math}) => {
    const {user} = useContext(AuthContext);

    //para guardar  la ruta actual usamos el el BOM con el window.locatio0, guradamos en el local storagen
    const nameUrl = window.location.pathname;
    localStorage.setItem('lastPath', nameUrl);

    return (
        <Router>
            <div>
                <Routes>
                    <Route path="/login/*" element={ user.logged ? (<Navigate to = '/' /> ):(<LoginPage /> )} />
                    <Route path="/*" element={ user.logged ? (<DashboardRouter />):(<Navigate to = '/login' /> )} />
                </Routes>
            </div>

        </Router>
    );
};

export default AppRouter;
