import {types} from "../types/types";

//funcion reducer en la q manejamos los action, lo q queremos hacer con cada uno y su retorno
export const authReducer = ( state = {}, action ) => {
    switch (action.type){
        case types.login:
            return{
                ...action.payload,//devuelvo los valores q estan en el payload
                logged: true//variable en true
            }
        case types.logout:
            return{
                logged: false
            }

        default:
            return state
    }
}
