import React from 'react';
import moment from 'moment';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import 'moment/locale/es'
import NavBar from "../ui/NavBar";
import {messages} from "../../helpers/calendar-message-en";
import CalendarEvent from "./CalendarEvent";

//para cambiar el idioma a espanol
moment.locale('es')
const localizer = momentLocalizer(moment);


const CalendarPage = () => {

    const events = [{
        title: 'cumpleannos del jefe',
        start: moment().toDate(),
        end: moment().add(2,'hours').toDate(),
        bg: '#fafafa',
        user: {
            _id: '123',
            name: 'Sheila'
        }
    }]



    //eventos para seleccionar, doble click
    const onDoubleClick = (e) => {
        console.log(e);
    }
    const onSelectEvent = (e) => {
        console.log(e);
    }
    const onViewChange = (e) => {
        setLastView(e);
        localStorage.setItem('lastView', e);
    }

    //style of events
    const eventStyleGetter = ( event, start, end, isSelected ) => {
        const style = {
            backgroundColor: '#367CF7',
            borderRadius: '0px',
            opacity: 0.8,
            display: 'block',
            color: 'white'
        }


        return {
            style
        }
    };


    return (
        <div className='calendar-screen'>
            <NavBar/>

            <Calendar
                localizer={ localizer }
                events={ events }
                startAccessor="start"
                endAccessor="end"
                messages = { messages } //para cambiar el idioma de lo que esta ahi a espannol
                eventPropGetter={ eventStyleGetter }
                //Creamos un componente aparte para el estilo de los eventos en el calendario,
                // para mostrar lo q quermos, y lo hacems asi
                components={{
                    event: CalendarEvent
                }}
                onDoubleClickEvent={ onDoubleClick }
                onSelectEvent={ onSelectEvent }
                onView={ onViewChange }
            />
        </div>
    );
};

export default CalendarPage;
