import React from 'react';
import {heroes} from "../data/heroes";
import HeroCard from "../heroes/HeroCard";
import {useForm} from "../../hooks/useForm";
import {useLocation, useParams} from "react-router";

const SearchPage = ({props}) => {
    const heroFiltrados = heroes;

    const [formValues, handleInputChange] = useForm({searchText:"" });
    //custom hook que se encarga de devolver los valores escritos en el input y el metodo que hace que esos valores
    // vallan cambianod

    const {searchText} = formValues;
    //desesctructrando el formValues para sacar el valor

   /* const loc = useLocation();
    const params = useParams();*/

    const history = useHistory();

    console.log(history.location.pathname); // '/about'


    const handleSearch = (e) =>{
        e.preventDefault();
        //sino ponemos esto se carga la pagina otra ves, con esto cancelamos el evento
        // eslint-disable-next-line react-hooks/rules-of-hooks
       /* console.log(params);
        console.log(loc);*/


    }

    return (
        <div className='mt-5'>
            <h1>Search Page</h1>
            <hr />

            <div className="row">

                <div className="col-5 mt-4">

                    <form onSubmit={handleSearch}>
                        <input
                            type="text"
                            placeholder="Find your hero"
                            className="form-control"
                            name="searchText"
                            autoComplete="off"
                            value={searchText}
                            onChange={handleInputChange}

                        />
                        <button
                            type="submit"
                            className="btn m-1 btn-block btn-outline-primary mt-3"
                        >
                            Search...
                        </button>
                    </form>
                </div>


                <div className="col-7">
                    {
                        heroFiltrados.map( hero => (
                            <HeroCard key={hero.id} {...hero} />
                        ))
                    }
                </div>
            </div>

        </div>
    );
};

export default SearchPage;
