            <div className="row">
                <div className="col-md-4">
                    <img src={ `./assets/${ id }.jpg` } className="card-img" alt={ superhero } />
                </div>
                <div className="col-md-8"> </div>

            </div>
        </div>
    );
};

export default HeroCard;
