import React from 'react';
import {heroes} from "../data/heroes";
import HeroCard from "../heroes/HeroCard";

const SearchPage = () => {
    const heroFiltrados = heroes;

    return (
        <div className='mt-5'>
            <h1>Search Page</h1>
            <hr />

            <div className="row">

                <div className="col-5">
                    <h4> Search Form </h4>
                    <hr />

                    <form>
                        <input
                            type="text"
                            placeholder="Find your hero"
                            className="form-control"
                            name="searchText"
                            autoComplete="off"

                        />

                        <button
                            type="submit"
                            className="btn m-1 btn-block btn-outline-primary"
                        >
                            Search...
                        </button>
                    </form>
                </div>


                <div className="col-7">
                    {
                        heroFiltrados.map( hero => (
                            <HeroCard ker={hero.id} {...hero} />
                        ))
                    }
                </div>
            </div>

        </div>
    );
};

export default SearchPage;
