import React, {useContext} from 'react';
import {Navbar} from "../components/ui/Navbar";
import {Navigate, Route, Routes} from "react-router-dom";
import MarvelPage from "../components/marvel/MarvelPage";
import DCPage from "../components/dc/DCPage";
import HeroesPage from "../components/heroes/HeroesPage";
import SearchPage from "../components/search/SearchPage";
import All from "../components/asociacion/All";

const DashboardRouter = () => {
    //para guardar  la ruta actual usamos el el BOM con el window.locatio0, guradamos en el local storagen
    const nameUrl = window.location.pathname;
    console.log(nameUrl)
    localStorage.setItem('lastPath', nameUrl);

    return (
        <div>
            <Navbar />

            <div className='container mt-2'>
                <Routes>
                    <Route exact path = "/marvel" element = { <MarvelPage />} />
                    <Route exact path = "/" element = { <All />} />
                    <Route exact path = "/dc/" element = { <DCPage />} />
                    <Route exact path = "/search" element = { <SearchPage />} />
                    <Route exact path="/hero/:heroeId" element={ <HeroesPage /> } />

                    <Route render={() => <Navigate to="/marvel" />} />
                </Routes>
            </div>
        </div>
    );
};


export default DashboardRouter;

//ruta hija del App Router


//al poner :heroeId, le estamos diciendo algo asi heroeID = al que sea su id, como que le damos el nombre
