import React, {useEffect, useReducer} from 'react';
import AppRouter from "./routers/AppRouter";
import {authReducer} from "./auth/authReducer";
import {AuthContext} from "./auth/AuthContext";

const init = () => {
    return JSON.parse(localStorage.getItem('user')) || { logged: false };
}

const HeroesApp = () => {

    //llamada del use reducer pasandole nuestra funcion reducer autReducer y el valor inicial quedandonos
    // la misma devuelve el usuario y la funcion dispach q permite disparar la accion
    const [user, dispatch] = useReducer( authReducer, {}, init);

    useEffect(() => {
        localStorage.setItem( 'user', JSON.stringify(user) );
    }, [user])


    return (
        //componente padre superior al q le pasaremos el valor q queremos compratir con todos sus hijos,
        // llamamos al context q creamos, todos los hijos tendran el user y el dispach, esto es 2 hooks juntos
        <AuthContext.Provider value = { {user, dispatch} }>
            <AppRouter />
        </AuthContext.Provider>
    );
};

export default HeroesApp;
