import React from 'react';
import {useParams} from "react-router";
import {getHeroById} from "../selectors/getHeroeById";
import {Navigate} from "react-router-dom";


const HeroesPage = () => {
    const {heroeId} = useParams();

    const hero = getHeroById(heroeId);

    if ( !hero ){
        return <Navigate to="/" />

    }

    const {superhero, alter_ego, first_appearance, characters,publisher} = hero;

    return (
        <div className="row mt-5">
            <div className="col-4">
                <img
                    src={ `../assets/${ heroeId }.jpg` }
                    alt={ superhero }
                    className="img-thumbnail animate__animated animate__fadeInLeft"
                />
            </div>

            <div className="col-8 animate__animated animate__fadeIn">
                <h3> { superhero } </h3>
                <ul className="list-group list-group-flush mt-4 p-0 ms-3">
                    <li className="list-group-item"> <b> Alter ego: </b> { alter_ego } </li>
                    <li className="list-group-item"> <b> Publisher: </b> { publisher } </li>
                    <li className="list-group-item"> <b> First appearance: </b> { first_appearance } </li>
                </ul>

                <h5 className="mt-4 ms-3"> Characters </h5>
                <p className="ms-3"> { characters } </p>

                <button className="btn btn-outline-info" >
                    Return
                </button>

            </div>

        </div>
    );
};

export default HeroesPage;
